import React, { useState, useMemo, useEffect } from 'react';
import { Plus, Edit2, Trash2, Save, X, Download, Upload, BarChart3, Package, Settings as SettingsIcon, MapPin, Truck, Store } from 'lucide-react';
import { useLocalStorage } from './hooks/useLocalStorage';
import Dashboard from './components/Dashboard';

/* ===================== CONFIG PRODUÃ‡ÃƒO ===================== */
const PRODUCAO_MAQUINAS_PADRAO = {
  TP40: 400,
  TP25: 300,
  FORRO: 100,
};

const COLAGEM_PRODUCAO = {
  TP40_30: 90,
  TP25_50: 100,
  FORRO_PIR30: 70,
};

const MAQUINAS = ['TP40', 'TP25', 'FORRO'];
const COLAGENS = [
  { label: 'Sem colagem', value: 'NENHUMA' },
  { label: 'TP40 30mm', value: 'TP40_30' },
  { label: 'TP25 50mm', value: 'TP25_50' },
  { label: 'FORRO PIR 30mm', value: 'FORRO_PIR30' },
];

const VENDEDORES = ['JoÃ£o', 'Maria', 'Carlos'];
const TIPOS_TELHAS = ['TP40', 'TP25', 'FORRO'];
const TIPOS_EPS = ['EPS', 'PIR', 'PU'];
const TIPOS_FRETE = ['CIF', 'FOB'];

/* JORNADA DE TRABALHO: 07:00 Ã s 17:00 (10 horas por dia) */
const HORAS_DIA = 10;
const INICIO_JORNADA = 7;
const DIAS_UTEIS_MES = 22;

/* ===================== FUNÃ‡Ã•ES AUX ===================== */
const hojeISO = () => new Date().toISOString().split('T')[0];

const calcularDataFinalPorHoras = (dataInicioISO, horasNecessarias) => {
  let data = new Date(dataInicioISO + 'T' + String(INICIO_JORNADA).padStart(2, '0') + ':00:00');
  let horasRestantes = horasNecessarias;

  while (horasRestantes > 0) {
    if (data.getDay() === 0) { // Domingo
      data.setDate(data.getDate() + 1);
      continue;
    }
    if (data.getDay() === 6) { // SÃ¡bado
      data.setDate(data.getDate() + 2);
      continue;
    }

    const horasNoDia = Math.min(horasRestantes, HORAS_DIA);
    horasRestantes -= horasNoDia;

    if (horasRestantes > 0) {
      data.setDate(data.getDate() + 1);
    }
  }

  return data.toISOString().split('T')[0];
};

const formatarDataBR = (iso) => {
  if (!iso) return '';
  const [a, m, d] = iso.split('-');
  return `${d}/${m}/${a}`;
};

/* ===================== COMPONENTE RELATÃ“RIO DE REGIÃ•ES ===================== */
const RelatorioRegioes = ({ pedidos = [] }) => {
  const resumo = useMemo(() => {
    const regioes = {};
    pedidos.forEach(p => {
      const reg = p.tipoEntrega === 'RETIRADA' ? 'RETIRADA NA LOJA' : (p.regiao || 'NÃƒO INFORMADO');
      if (!regioes[reg]) {
        regioes[reg] = { totalPedidos: 0, totalMetros: 0, clientes: new Set() };
      }
      regioes[reg].totalPedidos += 1;
      regioes[reg].totalMetros += parseFloat(p.totalMetros) || 0;
      regioes[reg].clientes.add(p.cliente);
    });
    return Object.entries(regioes).sort((a, b) => b[1].totalMetros - a[1].totalMetros);
  }, [pedidos]);

  return (
    <div className="space-y-6 animate-in fade-in duration-500">
      <h2 className="text-2xl font-bold text-gray-800 flex items-center gap-2">
        <MapPin className="text-red-500" /> RelatÃ³rio de RegiÃµes e LogÃ­stica
      </h2>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {resumo.slice(0, 3).map(([reg, dados], idx) => (
          <div key={reg} className="bg-white p-5 rounded-2xl shadow-sm border border-gray-100">
            <div className="flex justify-between items-start mb-2">
              <span className="text-xs font-black text-gray-400 uppercase tracking-widest">Top {idx + 1} RegiÃ£o</span>
              <MapPin size={16} className="text-blue-500" />
            </div>
            <h4 className="text-lg font-black text-blue-900 truncate">{reg}</h4>
            <p className="text-2xl font-black text-blue-600">{dados.totalMetros.toFixed(2)}m</p>
            <p className="text-xs text-gray-500 font-bold">{dados.totalPedidos} pedidos â€¢ {dados.clientes.size} clientes</p>
          </div>
        ))}
      </div>

      <div className="bg-white rounded-2xl shadow border border-gray-100 overflow-hidden">
        <table className="w-full text-sm">
          <thead className="bg-gray-50 border-b">
            <tr>
              <th className="px-6 py-4 text-left font-bold text-gray-600">RegiÃ£o / Localidade</th>
              <th className="px-6 py-4 text-center font-bold text-gray-600">Qtd Pedidos</th>
              <th className="px-6 py-4 text-center font-bold text-gray-600">Qtd Clientes</th>
              <th className="px-6 py-4 text-right font-bold text-gray-600">Total Metros</th>
            </tr>
          </thead>
          <tbody className="divide-y">
            {resumo.length === 0 ? (
              <tr><td colSpan="4" className="text-center py-10 text-gray-400 font-medium">Nenhum dado logÃ­stico disponÃ­vel</td></tr>
            ) : (
              resumo.map(([reg, dados]) => (
                <tr key={reg} className="hover:bg-gray-50 transition-colors">
                  <td className="px-6 py-4 font-bold text-blue-900">{reg}</td>
                  <td className="px-6 py-4 text-center font-medium">{dados.totalPedidos}</td>
                  <td className="px-6 py-4 text-center font-medium">{dados.clientes.size}</td>
                  <td className="px-6 py-4 text-right font-black text-blue-600">{dados.totalMetros.toFixed(2)}m</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

/* ===================== COMPONENTE SETTINGS ===================== */
const Settings = ({ metrosPorHora, setMetrosPorHora }) => {
  return (
    <div className="space-y-6 animate-in fade-in duration-500">
      <div className="bg-white p-6 rounded-2xl shadow-sm border border-gray-100">
        <h3 className="text-xl font-black mb-4 text-blue-900 flex items-center gap-2">
          <SettingsIcon size={24} /> ConfiguraÃ§Ãµes de Produtividade
        </h3>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {Object.entries(metrosPorHora).map(([tipo, metros]) => (
            <div key={tipo} className="bg-gray-50 p-4 rounded-xl border border-gray-200">
              <label className="block text-xs font-bold uppercase text-gray-400 mb-2 tracking-wider">{tipo}</label>
              <div className="flex items-center gap-3">
                <input 
                  type="number" 
                  value={metros} 
                  onChange={(e) => setMetrosPorHora({...metrosPorHora, [tipo]: parseFloat(e.target.value) || 0})} 
                  className="w-full border-gray-200 border rounded-xl px-4 py-2.5 focus:ring-2 focus:ring-blue-500 outline-none transition-all font-bold text-blue-900" 
                />
                <span className="text-sm font-black text-gray-400">m/h</span>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

/* ===================== APP ===================== */
const App = () => {
  const [activeTab, setActiveTab] = useState('pedidos');
  const [pedidos, setPedidos] = useLocalStorage('pedidos-mestre-aco', []);
  const [metrosPorHora, setMetrosPorHora] = useLocalStorage('config-produtividade', PRODUCAO_MAQUINAS_PADRAO);

  const [editingPedido, setEditingPedido] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [loadingCep, setLoadingCep] = useState(false);

  const [formData, setFormData] = useState({
    dataEntrada: hojeISO(),
    pedido: '',
    cliente: '',
    vendedor: VENDEDORES[0],
    tipoTelha: TIPOS_TELHAS[0],
    totalMetros: '',
    maquina: MAQUINAS[0],
    colagem: 'NENHUMA',
    tipoEps: TIPOS_EPS[0],
    tipoFrete: TIPOS_FRETE[0],
    tipoEntrega: 'ENTREGA', // 'ENTREGA' ou 'RETIRADA'
    cep: '',
    regiao: '',
    dataEntregaCliente: '',
    dataPrevistaProducao: '',
  });

  // Busca CEP via API
  useEffect(() => {
    const buscarCep = async () => {
      const cepLimpo = formData.cep.replace(/\D/g, '');
      if (cepLimpo.length === 8 && formData.tipoEntrega === 'ENTREGA') {
        setLoadingCep(true);
        try {
          const response = await fetch(`https://viacep.com.br/ws/${cepLimpo}/json/`);
          const data = await response.json();
          if (!data.erro) {
            setFormData(prev => ({ ...prev, regiao: `${data.localidade} - ${data.uf}` }));
          } else {
            alert('CEP nÃ£o encontrado.');
          }
        } catch (error) {
          console.error('Erro ao buscar CEP', error);
        } finally {
          setLoadingCep(false);
        }
      }
    };
    const timer = setTimeout(buscarCep, 800);
    return () => clearTimeout(timer);
  }, [formData.cep, formData.tipoEntrega]);

  const calcularMetricas = (pedido) => {
    const metros = parseFloat(pedido.totalMetros) || 0;
    const prodHora = metrosPorHora[pedido.maquina] || 0;
    const horaProducao = prodHora > 0 ? metros / prodHora : 0;

    let horaColagem = 0;
    if (pedido.colagem !== 'NENHUMA') {
      const prodColagem = COLAGEM_PRODUCAO[pedido.colagem] || 0;
      horaColagem = prodColagem > 0 ? metros / prodColagem : 0;
    }

    const tempoTotal = horaProducao + horaColagem;
    const dataInicio = hojeISO();
    const dataPrevista = calcularDataFinalPorHoras(dataInicio, tempoTotal);
    const diasNecessarios = Math.ceil(tempoTotal / HORAS_DIA);

    return {
      horaProducao: horaProducao.toFixed(2),
      horaColagem: horaColagem.toFixed(2),
      tempoTotal: tempoTotal.toFixed(2),
      dataPrevista,
      diasNecessarios,
    };
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.pedido || !formData.cliente || !formData.totalMetros) {
      alert('Preencha os campos obrigatÃ³rios.');
      return;
    }
    const metricas = calcularMetricas(formData);
    const novoPedido = {
      ...formData,
      id: editingPedido?.id || Date.now(),
      horaProducao: metricas.horaProducao,
      horaColagem: metricas.horaColagem,
      tempoTotalProducao: metricas.tempoTotal,
      prazoProducaoDias: metricas.diasNecessarios,
      dataPrevistaProducao: metricas.dataPrevista,
    };
    if (editingPedido) {
      setPedidos(pedidos.map(p => p.id === editingPedido.id ? novoPedido : p));
    } else {
      setPedidos([...pedidos, novoPedido]);
    }
    resetForm();
  };

  const resetForm = () => {
    setFormData({
      dataEntrada: hojeISO(),
      pedido: '',
      cliente: '',
      vendedor: VENDEDORES[0],
      tipoTelha: TIPOS_TELHAS[0],
      totalMetros: '',
      maquina: MAQUINAS[0],
      colagem: 'NENHUMA',
      tipoEps: TIPOS_EPS[0],
      tipoFrete: TIPOS_FRETE[0],
      tipoEntrega: 'ENTREGA',
      cep: '',
      regiao: '',
      dataEntregaCliente: '',
      dataPrevistaProducao: '',
    });
    setEditingPedido(null);
    setShowForm(false);
  };

  const handleEdit = (pedido) => {
    setFormData(pedido);
    setEditingPedido(pedido);
    setShowForm(true);
  };

  const handleDelete = (id) => {
    if (window.confirm('Excluir este pedido?')) {
      setPedidos(pedidos.filter(p => p.id !== id));
    }
  };

  const exportarDados = () => {
    const dataToExport = { pedidos, metrosPorHora, exportDate: new Date().toISOString() };
    const blob = new Blob([JSON.stringify(dataToExport, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `mestre-aco-logistica-${new Date().toISOString().split('T')[0]}.json`;
    a.click();
    URL.revokeObjectURL(url);
  };

  const importarDados = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        try {
          const dados = JSON.parse(event.target.result);
          if (Array.isArray(dados.pedidos)) {
            setPedidos(dados.pedidos);
            if (dados.metrosPorHora) setMetrosPorHora(dados.metrosPorHora);
          }
        } catch (error) { alert('Erro ao importar.'); }
      };
      reader.readAsText(file);
    }
  };

  const totalHorasDisponiveisMes = HORAS_DIA * DIAS_UTEIS_MES * MAQUINAS.length;
  const resumoDashboard = useMemo(() => {
    const porMaquina = {};
    MAQUINAS.forEach(m => porMaquina[m] = { horasUsadas: 0, pedidos: [] });
    (Array.isArray(pedidos) ? pedidos : []).forEach(p => {
      if (!porMaquina[p.maquina]) return;
      porMaquina[p.maquina].horasUsadas += Number(p.tempoTotalProducao || 0);
      porMaquina[p.maquina].pedidos.push(p);
    });
    const resultado = {};
    MAQUINAS.forEach(m => {
      const capacidade = HORAS_DIA * DIAS_UTEIS_MES;
      const usadas = porMaquina[m].horasUsadas;
      resultado[m] = { capacidade, usadas, saldo: capacidade - usadas, pedidos: porMaquina[m].pedidos };
    });
    return resultado;
  }, [pedidos]);

  const totalHorasUsadas = (Array.isArray(pedidos) ? pedidos : []).reduce((acc, p) => acc + Number(p.tempoTotalProducao || 0), 0);
  const saldoHoras = totalHorasDisponiveisMes - totalHorasUsadas;

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-blue-900 text-white p-6 shadow-xl">
        <div className="max-w-7xl mx-auto flex flex-col md:flex-row justify-between items-center gap-4">
          <div className="flex items-center gap-4">
            <div className="bg-white p-2 rounded-xl shadow-inner"><Package className="text-blue-900" size={32} /></div>
            <div>
              <h1 className="text-3xl font-black tracking-tight">MESTRE AÃ‡O SP</h1>
              <p className="text-blue-200 text-sm font-medium uppercase tracking-widest">LogÃ­stica & ProduÃ§Ã£o v5.0</p>
            </div>
          </div>
          <nav className="flex bg-blue-800/50 p-1 rounded-xl backdrop-blur-sm">
            <button onClick={() => setActiveTab('pedidos')} className={`flex items-center gap-2 px-6 py-2 rounded-lg transition-all ${activeTab === 'pedidos' ? 'bg-white text-blue-900 shadow-lg' : 'text-blue-100 hover:bg-blue-700/50'}`}><Package size={18} /> <span className="font-bold">Pedidos</span></button>
            <button onClick={() => setActiveTab('dashboard')} className={`flex items-center gap-2 px-6 py-2 rounded-lg transition-all ${activeTab === 'dashboard' ? 'bg-white text-blue-900 shadow-lg' : 'text-blue-100 hover:bg-blue-700/50'}`}><BarChart3 size={18} /> <span className="font-bold">Dashboard</span></button>
            <button onClick={() => setActiveTab('regioes')} className={`flex items-center gap-2 px-6 py-2 rounded-lg transition-all ${activeTab === 'regioes' ? 'bg-white text-blue-900 shadow-lg' : 'text-blue-100 hover:bg-blue-700/50'}`}><MapPin size={18} /> <span className="font-bold">RegiÃµes</span></button>
            <button onClick={() => setActiveTab('config')} className={`flex items-center gap-2 px-6 py-2 rounded-lg transition-all ${activeTab === 'config' ? 'bg-white text-blue-900 shadow-lg' : 'text-blue-100 hover:bg-blue-700/50'}`}><SettingsIcon size={18} /> <span className="font-bold">Config</span></button>
          </nav>
        </div>
      </header>

      <main className="max-w-7xl mx-auto p-4 md:p-6">
        {activeTab === 'pedidos' && (
          <div className="space-y-6 animate-in fade-in duration-500">
            <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4">
              <h2 className="text-2xl font-bold text-gray-800">Carteira de Pedidos</h2>
              <div className="flex flex-wrap gap-2">
                <button onClick={() => setShowForm(true)} className="flex items-center gap-2 bg-blue-600 text-white px-5 py-2.5 rounded-xl hover:bg-blue-700 transition-colors shadow-md font-bold"><Plus size={20} /> Novo Pedido</button>
                <button onClick={exportarDados} className="flex items-center gap-2 bg-emerald-600 text-white px-5 py-2.5 rounded-xl hover:bg-emerald-700 transition-colors shadow-md font-bold"><Download size={20} /> Exportar</button>
                <label className="flex items-center gap-2 bg-amber-600 text-white px-5 py-2.5 rounded-xl hover:bg-amber-700 transition-colors shadow-md font-bold cursor-pointer"><Upload size={20} /> Importar<input type="file" accept=".json" onChange={importarDados} className="hidden" /></label>
              </div>
            </div>

            {showForm && (
              <div className="bg-white p-6 rounded-2xl shadow-2xl border-2 border-blue-100 animate-in zoom-in duration-300">
                <div className="flex justify-between items-center mb-6">
                  <h3 className="text-xl font-black text-blue-900">{editingPedido ? 'ðŸ“ Editar Pedido' : 'ðŸ†• Novo Pedido'}</h3>
                  <button onClick={resetForm} className="p-2 hover:bg-gray-100 rounded-full transition-colors text-gray-400"><X size={24} /></button>
                </div>
                <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div className="space-y-1"><label className="text-xs font-bold text-gray-500 uppercase ml-1">Data Entrada</label><input type="date" value={formData.dataEntrada} onChange={(e) => setFormData({ ...formData, dataEntrada: e.target.value })} className="w-full border-gray-200 border rounded-xl px-4 py-2.5 focus:ring-2 focus:ring-blue-500 outline-none transition-all" /></div>
                  <div className="space-y-1"><label className="text-xs font-bold text-gray-500 uppercase ml-1">NÂº Pedido *</label><input type="text" required value={formData.pedido} onChange={(e) => setFormData({ ...formData, pedido: e.target.value })} className="w-full border-gray-200 border rounded-xl px-4 py-2.5 focus:ring-2 focus:ring-blue-500 outline-none transition-all" placeholder="Ex: 12345" /></div>
                  <div className="space-y-1"><label className="text-xs font-bold text-gray-500 uppercase ml-1">Cliente *</label><input type="text" required value={formData.cliente} onChange={(e) => setFormData({ ...formData, cliente: e.target.value })} className="w-full border-gray-200 border rounded-xl px-4 py-2.5 focus:ring-2 focus:ring-blue-500 outline-none transition-all" placeholder="Nome do cliente" /></div>

                  <div className="space-y-1">
