    totalHoraColagem: pedidos.reduce((acc, p) => acc + (parseFloat(p.horaColagem) || 0), 0).toFixed(2),
    totalProducao: pedidos.reduce((acc, p) => acc + (parseFloat(p.tempoTotalProducao) || 0), 0).toFixed(2),
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-blue-900 text-white p-6 shadow-xl">
        <div className="max-w-7xl mx-auto flex flex-col md:flex-row justify-between items-center gap-4">
          <div className="flex items-center gap-4">
            <div className="bg-white p-2 rounded-xl shadow-inner">
              <Package className="text-blue-900" size={32} />
            </div>
            <div>
              <h1 className="text-3xl font-black tracking-tight">MESTRE AÃ‡O SP</h1>
              <p className="text-blue-200 text-sm font-medium uppercase tracking-widest">GestÃ£o de ProduÃ§Ã£o v4.1</p>
            </div>
          </div>

          <nav className="flex bg-blue-800/50 p-1 rounded-xl backdrop-blur-sm">
            <button
              onClick={() => setActiveTab('pedidos')}
              className={`flex items-center gap-2 px-6 py-2 rounded-lg transition-all ${activeTab === 'pedidos' ? 'bg-white text-blue-900 shadow-lg' : 'text-blue-100 hover:bg-blue-700/50'}`}
            >
              <Package size={18} /> <span className="font-bold">Pedidos</span>
            </button>
            <button
              onClick={() => setActiveTab('dashboard')}
              className={`flex items-center gap-2 px-6 py-2 rounded-lg transition-all ${activeTab === 'dashboard' ? 'bg-white text-blue-900 shadow-lg' : 'text-blue-100 hover:bg-blue-700/50'}`}
            >
              <BarChart3 size={18} /> <span className="font-bold">Dashboard</span>
            </button>
            <button
              onClick={() => setActiveTab('horas')}
              className={`flex items-center gap-2 px-6 py-2 rounded-lg transition-all ${activeTab === 'horas' ? 'bg-white text-blue-900 shadow-lg' : 'text-blue-100 hover:bg-blue-700/50'}`}
            >
              <Clock size={18} /> <span className="font-bold">Horas MÃ¡quina</span>
            </button>
            <button
              onClick={() => setActiveTab('config')}
              className={`flex items-center gap-2 px-6 py-2 rounded-lg transition-all ${activeTab === 'config' ? 'bg-white text-blue-900 shadow-lg' : 'text-blue-100 hover:bg-blue-700/50'}`}
            >
              <SettingsIcon size={18} /> <span className="font-bold">Config</span>
            </button>
          </nav>
        </div>
      </header>

      <main className="max-w-7xl mx-auto p-4 md:p-6">
        {activeTab === 'pedidos' && (
          <div className="space-y-6 animate-in fade-in duration-500">
            {/* Toolbar */}
            <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4">
              <h2 className="text-2xl font-bold text-gray-800">Carteira de Pedidos</h2>
              <div className="flex flex-wrap gap-2">
                <button onClick={() => setShowForm(true)} className="flex items-center gap-2 bg-blue-600 text-white px-5 py-2.5 rounded-xl hover:bg-blue-700 transition-colors shadow-md font-bold">
                  <Plus size={20} /> Novo Pedido
                </button>
                <button onClick={exportarDados} className="flex items-center gap-2 bg-emerald-600 text-white px-5 py-2.5 rounded-xl hover:bg-emerald-700 transition-colors shadow-md font-bold">
                  <Download size={20} /> Exportar
                </button>
                <label className="flex items-center gap-2 bg-amber-600 text-white px-5 py-2.5 rounded-xl hover:bg-amber-700 transition-colors shadow-md font-bold cursor-pointer">
                  <Upload size={20} /> Importar
                  <input type="file" accept=".json" onChange={importarDados} className="hidden" />
                </label>
              </div>
            </div>

            {/* Stats Cards */}
            <div className="grid grid-cols-2 lg:grid-cols-5 gap-4">
              {[
                { label: 'Total Pedidos', value: resumo.totalPedidos, unit: '', color: 'blue' },
                { label: 'Total Metros', value: resumo.totalMetros, unit: 'm', color: 'indigo' },
                { label: 'Horas ProduÃ§Ã£o', value: resumo.totalHoraProducao, unit: 'h', color: 'violet' },
                { label: 'Horas Colagem', value: resumo.totalHoraColagem, unit: 'h', color: 'purple' },
                { label: 'ProduÃ§Ã£o Total', value: resumo.totalProducao, unit: 'h', color: 'blue' },
              ].map((stat, idx) => (
                <div key={idx} className="bg-white p-4 rounded-2xl shadow-sm border border-gray-100 flex flex-col items-center justify-center text-center">
                  <span className="text-xs font-bold text-gray-400 uppercase tracking-wider mb-1">{stat.label}</span>
                  <span className={`text-2xl font-black text-${stat.color}-600`}>{stat.value}{stat.unit}</span>
                </div>
              ))}
            </div>

            {/* Form */}
            {showForm && (
              <div className="bg-white p-6 rounded-2xl shadow-2xl border-2 border-blue-100 animate-in zoom-in duration-300">
                <div className="flex justify-between items-center mb-6">
                  <h3 className="text-xl font-black text-blue-900">{editingPedido ? 'ðŸ“ Editar Pedido' : 'ðŸ†• Novo Pedido'}</h3>
                  <button onClick={resetForm} className="p-2 hover:bg-gray-100 rounded-full transition-colors text-gray-400"><X size={24} /></button>
                </div>
                <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div className="space-y-1">
                    <label className="text-xs font-bold text-gray-500 uppercase ml-1">Data Entrada</label>
                    <input type="date" value={formData.dataEntrada} onChange={(e) => setFormData({ ...formData, dataEntrada: e.target.value })} className="w-full border-gray-200 border rounded-xl px-4 py-2.5 focus:ring-2 focus:ring-blue-500 outline-none transition-all" />
                  </div>
                  <div className="space-y-1">
                    <label className="text-xs font-bold text-gray-500 uppercase ml-1">NÂº Pedido *</label>
                    <input type="text" required value={formData.pedido} onChange={(e) => setFormData({ ...formData, pedido: e.target.value })} className="w-full border-gray-200 border rounded-xl px-4 py-2.5 focus:ring-2 focus:ring-blue-500 outline-none transition-all" placeholder="Ex: 12345" />
                  </div>
                  <div className="space-y-1">
                    <label className="text-xs font-bold text-gray-500 uppercase ml-1">Cliente *</label>
                    <input type="text" required value={formData.cliente} onChange={(e) => setFormData({ ...formData, cliente: e.target.value })} className="w-full border-gray-200 border rounded-xl px-4 py-2.5 focus:ring-2 focus:ring-blue-500 outline-none transition-all" placeholder="Nome do cliente" />
                  </div>

                  <div className="space-y-1">
                    <label className="text-xs font-bold text-gray-500 uppercase ml-1">MÃ¡quina</label>
                    <select value={formData.maquina} onChange={(e) => setFormData({ ...formData, maquina: e.target.value })} className="w-full border-gray-200 border rounded-xl px-4 py-2.5 focus:ring-2 focus:ring-blue-500 outline-none transition-all bg-white">
                      {MAQUINAS.map(m => <option key={m} value={m}>{m}</option>)}
                    </select>
                  </div>

                  <div className="space-y-1">
                    <label className="text-xs font-bold text-gray-500 uppercase ml-1">Colagem</label>
                    <select value={formData.colagem} onChange={(e) => setFormData({ ...formData, colagem: e.target.value })} className="w-full border-gray-200 border rounded-xl px-4 py-2.5 focus:ring-2 focus:ring-blue-500 outline-none transition-all bg-white">
                      {COLAGENS.map(c => <option key={c.value} value={c.value}>{c.label}</option>)}
                    </select>
                  </div>

                  <div className="space-y-1">
                    <label className="text-xs font-bold text-gray-500 uppercase ml-1">Total Metros *</label>
                    <input type="number" step="0.01" required value={formData.totalMetros} onChange={(e) => setFormData({ ...formData, totalMetros: e.target.value })} className="w-full border-gray-200 border rounded-xl px-4 py-2.5 focus:ring-2 focus:ring-blue-500 outline-none transition-all" placeholder="0.00" />
                  </div>

                  {/* DATA PREVISTA AUTOMÃTICA */}
                  <div className="space-y-1">
                    <label className="text-xs font-bold text-gray-500 uppercase ml-1">PrevisÃ£o ProduÃ§Ã£o</label>
                    <input
                      type="text"
                      disabled
                      value={formatarDataBR(formData.dataPrevistaProducao)}
                      className="w-full bg-gray-100 border-gray-200 border rounded-xl px-4 py-2.5 font-bold text-green-700"
                    />
                  </div>

                  <div className="space-y-1">
                    <label className="text-xs font-bold text-gray-500 uppercase ml-1">Tipo de EPS</label>
                    <select value={formData.tipoEps} onChange={(e) => setFormData({ ...formData, tipoEps: e.target.value })} className="w-full border-gray-200 border rounded-xl px-4 py-2.5 focus:ring-2 focus:ring-blue-500 outline-none transition-all bg-white">
                      {TIPOS_EPS.map(e => <option key={e} value={e}>{e}</option>)}
                    </select>
                  </div>

                  <div className="space-y-1">
                    <label className="text-xs font-bold text-gray-500 uppercase ml-1">Tipo de Frete</label>
                    <select value={formData.tipoFrete} onChange={(e) => setFormData({ ...formData, tipoFrete: e.target.value })} className="w-full border-gray-200 border rounded-xl px-4 py-2.5 focus:ring-2 focus:ring-blue-500 outline-none transition-all bg-white">
                      {TIPOS_FRETE.map(f => <option key={f} value={f}>{f}</option>)}
                    </select>
                  </div>

                  <div className="space-y-1">
                    <label className="text-xs font-bold text-gray-500 uppercase ml-1">RegiÃ£o</label>
                    <input type="text" value={formData.regiao} onChange={(e) => setFormData({ ...formData, regiao: e.target.value })} className="w-full border-gray-200 border rounded-xl px-4 py-2.5 focus:ring-2 focus:ring-blue-500 outline-none transition-all" placeholder="RegiÃ£o de entrega" />
                  </div>

                  <div className="space-y-1">
                    <label className="text-xs font-bold text-gray-500 uppercase ml-1">Data Entrega Cliente</label>
                    <input type="date" value={formData.dataEntregaCliente} onChange={(e) => setFormData({ ...formData, dataEntregaCliente: e.target.value })} className="w-full border-gray-200 border rounded-xl px-4 py-2.5 focus:ring-2 focus:ring-blue-500 outline-none transition-all" />
                  </div>

                  <div className="md:col-span-3 flex justify-end gap-3 mt-4 pt-4 border-t">
                    <button type="button" onClick={resetForm} className="px-6 py-2.5 border border-gray-300 rounded-xl hover:bg-gray-50 transition-colors font-bold text-gray-600">Cancelar</button>
                    <button type="submit" className="flex items-center gap-2 bg-blue-900 text-white px-8 py-2.5 rounded-xl hover:bg-blue-800 transition-colors shadow-lg font-bold">
                      <Save size={20} /> Salvar Pedido
                    </button>
                  </div>
                </form>
              </div>
            )}

            {/* Table */}
            <div className="bg-white rounded-2xl shadow-sm border border-gray-100 overflow-hidden">
              <div className="overflow-x-auto">
                <table className="w-full text-sm text-left">
                  <thead>
                    <tr className="bg-gray-50 border-b border-gray-100">
                      <th className="px-4 py-4 font-bold text-gray-600">Pedido</th>
                      <th className="px-4 py-4 font-bold text-gray-600">Cliente</th>
                      <th className="px-4 py-4 font-bold text-gray-600">MÃ¡quina</th>
                      <th className="px-4 py-4 font-bold text-gray-600">Colagem</th>
                      <th className="px-4 py-4 font-bold text-gray-600 text-right">Metros</th>
                      <th className="px-4 py-4 font-bold text-gray-600 text-right">Horas Total</th>
                      <th className="px-4 py-4 font-bold text-gray-600 text-right">PrevisÃ£o</th>
                      <th className="px-4 py-4 font-bold text-gray-600 text-center">AÃ§Ãµes</th>
                    </tr>
                  </thead>
                  <tbody className="divide-y divide-gray-50">
                    {pedidos.map((p) => (
                      <tr key={p.id} className="hover:bg-blue-50/30 transition-colors group">
                        <td className="px-4 py-4 font-black text-blue-900">{p.pedido}</td>
