        {activeTab === 'horas' && <RelatorioHorasMaquina pedidos={pedidos} />}
        {activeTab === 'config' && <Settings />}
      </main>

      <footer className="max-w-7xl mx-auto p-6 text-center text-gray-400 text-xs font-medium uppercase tracking-widest">
        &copy; {new Date().getFullYear()} Mestre AÃ§o SP - Sistema Interno de ProduÃ§Ã£o
      </footer>
    </div>
  );
};

export default App;
