import React from 'react';
import { BarChart3 } from 'lucide-react';
import { VENDEDORES, TIPOS_EPS } from '../utils/constants';

const Dashboard = ({ pedidos }) => {
  if (pedidos.length === 0) {
    return (
      <div className="bg-white p-12 rounded-lg shadow text-center">
        <BarChart3 size={64} className="mx-auto text-gray-300 mb-4" />
        <p className="text-gray-500 text-lg">Adicione pedidos para ver estatísticas</p>
      </div>
    );
  }

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
      <div className="bg-white p-6 rounded-lg shadow">
        <h3 className="text-xl font-bold mb-4 text-blue-800">Performance por Vendedor</h3>
        <div className="space-y-2">
          {VENDEDORES.map(v => {
            const pv = pedidos.filter(p => p.vendedor === v);
            const m = pv.reduce((a, p) => a + (parseFloat(p.totalMetros) || 0), 0);
            return pv.length > 0 ? (
              <div key={v} className="flex justify-between items-center p-3 bg-blue-50 rounded">
                <span className="font-medium">{v}</span>
                <div className="text-right">
                  <div className="text-sm text-gray-600">{pv.length} pedidos</div>
                  <div className="font-bold text-blue-900">{m.toFixed(2)}m</div>
                </div>
              </div>
            ) : null;
          })}
        </div>
      </div>
      <div className="bg-white p-6 rounded-lg shadow">
        <h3 className="text-xl font-bold mb-4 text-blue-800">Produção por EPS</h3>
        <div className="space-y-2">
          {TIPOS_EPS.map(eps => {
            const pe = pedidos.filter(p => p.tipoEps === eps);
            const m = pe.reduce((a, p) => a + (parseFloat(p.totalMetros) || 0), 0);
            return pe.length > 0 ? (
              <div key={eps} className="flex justify-between items-center p-3 bg-green-50 rounded">
                <span className="font-medium">{eps}</span>
                <div className="text-right">
                  <div className="text-sm text-gray-600">{pe.length} pedidos</div>
                  <div className="font-bold text-green-900">{m.toFixed(2)}m</div>
                </div>
              </div>
            ) : null;
          })}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
