import React, { useState } from 'react';
import { Plus, Edit2, Trash2, Save, X, Download, Upload, BarChart3, Package, Settings as SettingsIcon } from 'lucide-react';
import { useLocalStorage } from './hooks/useLocalStorage';
import { VENDEDORES, TIPOS_TELHAS, TIPOS_EPS, TIPOS_FRETE, DEFAULT_METROS_POR_HORA } from './utils/constants';
import Dashboard from './components/Dashboard';
import Settings from './components/Settings';

const App = () => {
  const [activeTab, setActiveTab] = useState('pedidos');
  const [pedidos, setPedidos] = useLocalStorage('pedidos-mestre-aco', []);
  const [metrosPorHora, setMetrosPorHora] = useLocalStorage('config-mestre-aco', DEFAULT_METROS_POR_HORA);

  const [editingPedido, setEditingPedido] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    dataEntrada: new Date().toISOString().split('T')[0],
    pedido: '', cliente: '', vendedor: VENDEDORES[0], tipoTelha: TIPOS_TELHAS[0],
    totalMetros: '', maquina: '', tipoEps: TIPOS_EPS[0], tipoFrete: TIPOS_FRETE[0],
    regiao: '', dataEntregaCliente: '', dataSugerida: ''
  });

  const calcularMetricas = (pedido) => {
    const metros = parseFloat(pedido.totalMetros) || 0;
    const metrosHora = metrosPorHora[pedido.tipoEps] || 0;
    const horaMaquina = metrosHora > 0 ? metros / metrosHora : 0;
    const horaMontagem = horaMaquina;
    const tempoTotal = horaMaquina + horaMontagem;
    return {
      horaMaquina: horaMaquina.toFixed(2),
      horaMontagem: horaMontagem.toFixed(2),
      tempoTotal: tempoTotal.toFixed(2)
    };
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.pedido || !formData.cliente || !formData.totalMetros) {
      alert('Preencha os campos obrigatÃ³rios: Pedido, Cliente e Total Metros');
      return;
    }
    const metricas = calcularMetricas(formData);
    const novoPedido = {
      ...formData, 
      id: editingPedido?.id || Date.now(),
      horaMaquina: metricas.horaMaquina,
      horaMontagem: metricas.horaMontagem,
      tempoTotalProducao: metricas.tempoTotal
    };

    if (editingPedido) {
      setPedidos(pedidos.map(p => p.id === editingPedido.id ? novoPedido : p));
    } else {
      setPedidos([...pedidos, novoPedido]);
    }
    resetForm();
  };

  const resetForm = () => {
    setFormData({
      dataEntrada: new Date().toISOString().split('T')[0],
      pedido: '', cliente: '', vendedor: VENDEDORES[0], tipoTelha: TIPOS_TELHAS[0],
      totalMetros: '', maquina: '', tipoEps: TIPOS_EPS[0], tipoFrete: TIPOS_FRETE[0],
      regiao: '', dataEntregaCliente: '', dataSugerida: ''
    });
    setEditingPedido(null);
    setShowForm(false);
  };

  const handleEdit = (pedido) => {
    setFormData(pedido);
    setEditingPedido(pedido);
    setShowForm(true);
  };

  const handleDelete = (id) => {
    if (window.confirm('Tem certeza que deseja excluir este pedido?')) {
      setPedidos(pedidos.filter(p => p.id !== id));
    }
  };

  const exportarDados = () => {
    const dataToExport = { pedidos, metrosPorHora, exportDate: new Date().toISOString() };
    const blob = new Blob([JSON.stringify(dataToExport, null, 2)], {type: 'application/json'});
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `mestre-aco-backup-${new Date().toISOString().split('T')[0]}.json`;
    a.click();
    URL.revokeObjectURL(url);
  };

  const importarDados = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        try {
          const dados = JSON.parse(event.target.result);
          if (dados.pedidos) setPedidos(dados.pedidos);
          if (dados.metrosPorHora) setMetrosPorHora(dados.metrosPorHora);
          alert('Dados importados com sucesso!');
        } catch (error) {
          alert('Erro ao importar arquivo. Verifique o formato.');
        }
      };
      reader.readAsText(file);
    }
  };

  const resumo = {
    totalPedidos: pedidos.length,
    totalMetros: pedidos.reduce((acc, p) => acc + (parseFloat(p.totalMetros) || 0), 0).toFixed(2),
    totalHoraMaquina: pedidos.reduce((acc, p) => acc + (parseFloat(p.horaMaquina) || 0), 0).toFixed(2),
    totalHoraMontagem: pedidos.reduce((acc, p) => acc + (parseFloat(p.horaMontagem) || 0), 0).toFixed(2),
    totalProducao: pedidos.reduce((acc, p) => acc + (parseFloat(p.tempoTotalProducao) || 0), 0).toFixed(2)
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-blue-900 text-white p-6 shadow-xl">
        <div className="max-w-7xl mx-auto flex flex-col md:flex-row justify-between items-center gap-4">
          <div className="flex items-center gap-4">
            <div className="bg-white p-2 rounded-xl shadow-inner">
              <Package className="text-blue-900" size={32} />
            </div>
            <div>
