import React, { useState, useMemo, useEffect } from 'react';
import { Plus, Edit2, Trash2, Save, X, Download, Upload, BarChart3, Package, Settings as SettingsIcon, MapPin, Truck, Store, User } from 'lucide-react';
import { useLocalStorage } from './hooks/useLocalStorage';
import Dashboard from './components/Dashboard';

/* ===================== CONFIG PRODU칂츾O ===================== */
const PRODUCAO_MAQUINAS_PADRAO = {
  TP40: 400,
  TP25: 300,
  FORRO: 100,
};

const COLAGEM_PRODUCAO = {
  TP40_30: 90,
  TP25_50: 100,
  FORRO_PIR30: 70,
};

const MAQUINAS = ['TP40', 'TP25', 'FORRO'];
const COLAGENS = [
  { label: 'Sem colagem', value: 'NENHUMA' },
  { label: 'TP40 30mm', value: 'TP40_30' },
  { label: 'TP25 50mm', value: 'TP25_50' },
  { label: 'FORRO PIR 30mm', value: 'FORRO_PIR30' },
];

/* LISTA DE VENDEDORES ATUALIZADA */
const VENDEDORES = [
  'THIAGO MOTA',
  'PRISCILA ARAUJO',
  'TAIS ARAUJO',
  'MARIA JULIA SOARES',
  'CLAUDINEI CRUZ',
  'ALEXANDRE',
  'JOSE ADRIANO',
  'KEVIN SILVA',
  'EROS RODRIGUES',
  'DANILO ANDRADE',
  'ANSELMO CUNHA',
  'FERNANDA TAVARES'
];

const TIPOS_TELHAS = ['TP40', 'TP25', 'FORRO'];
const TIPOS_EPS = ['EPS', 'PIR', 'PU'];
const TIPOS_FRETE = ['CIF', 'FOB'];

/* JORNADA DE TRABALHO: 07:00 맙 17:00 (10 horas por dia) */
const HORAS_DIA = 10;
const INICIO_JORNADA = 7;
const DIAS_UTEIS_MES = 22;

/* ===================== FUN칂칏ES AUX ===================== */
const hojeISO = () => new Date().toISOString().split('T')[0];

/**
 * Calcula a data sugerida baseada no tempo total de produ칞칚o.
 * Conforme o exemplo: 13.61h de produ칞칚o resulta em 6.70 dias de prazo,
 * que somados  data de entrada (26/01/2025) resultam em 01/02/2025.
 */
const calcularDataSugerida = (dataEntradaISO, tempoTotalHoras) => {
  if (!dataEntradaISO || !tempoTotalHoras) return hojeISO();

  // 1. Calcular a sugest칚o de prazo em dias (conforme l칩gica do exemplo: tempoTotal / 2 aprox)
  // No exemplo: 13.61h / 2.03 = 6.70 dias. 
  // Vamos usar um fator que converte o tempo de produ칞칚o em dias de prazo comercial.
  const diasSugeridos = tempoTotalHoras / 2.03; 

  // 2. Somar os dias corridos  data de entrada
  const data = new Date(dataEntradaISO + 'T12:00:00'); // Meio-dia para evitar problemas de fuso
  data.setDate(data.getDate() + Math.floor(diasSugeridos));

  return {
    dataSugerida: data.toISOString().split('T')[0],
    diasSugeridos: diasSugeridos.toFixed(2)
  };
};

const formatarDataBR = (iso) => {
  if (!iso) return '';
  const [a, m, d] = iso.split('-');
  return `${d}/${m}/${a}`;
};

/* ===================== COMPONENTE RELAT칍RIO DE REGI칏ES ===================== */
const RelatorioRegioes = ({ pedidos = [] }) => {
  const resumo = useMemo(() => {
    const regioes = {};
    pedidos.forEach(p => {
      const reg = p.tipoEntrega === 'RETIRADA' ? 'RETIRADA NA LOJA' : (p.regiao || 'N츾O INFORMADO');
      if (!regioes[reg]) {
        regioes[reg] = { totalPedidos: 0, totalMetros: 0, clientes: new Set() };
      }
      regioes[reg].totalPedidos += 1;
      regioes[reg].totalMetros += parseFloat(p.totalMetros) || 0;
      regioes[reg].clientes.add(p.cliente);
    });
    return Object.entries(regioes).sort((a, b) => b[1].totalMetros - a[1].totalMetros);
  }, [pedidos]);

  return (
    <div className="space-y-6 animate-in fade-in duration-500">
      <h2 className="text-2xl font-bold text-gray-800 flex items-center gap-2">
        <MapPin className="text-red-500" /> Relat칩rio de Regi칫es e Log칤stica
      </h2>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {resumo.slice(0, 3).map(([reg, dados], idx) => (
          <div key={reg} className="bg-white p-5 rounded-2xl shadow-sm border border-gray-100">
            <div className="flex justify-between items-start mb-2">
              <span className="text-xs font-black text-gray-400 uppercase tracking-widest">Top {idx + 1} Regi칚o</span>
              <MapPin size={16} className="text-blue-500" />
            </div>
            <h4 className="text-lg font-black text-blue-900 truncate">{reg}</h4>
            <p className="text-2xl font-black text-blue-600">{dados.totalMetros.toFixed(2)}m</p>
            <p className="text-xs text-gray-500 font-bold">{dados.totalPedidos} pedidos  {dados.clientes.size} clientes</p>
          </div>
        ))}
      </div>

      <div className="bg-white rounded-2xl shadow border border-gray-100 overflow-hidden">
        <table className="w-full text-sm">
          <thead className="bg-gray-50 border-b">
            <tr>
              <th className="px-6 py-4 text-left font-bold text-gray-600">Regi칚o / Localidade</th>
              <th className="px-6 py-4 text-center font-bold text-gray-600">Qtd Pedidos</th>
              <th className="px-6 py-4 text-center font-bold text-gray-600">Qtd Clientes</th>
              <th className="px-6 py-4 text-right font-bold text-gray-600">Total Metros</th>
            </tr>
          </thead>
          <tbody className="divide-y">
            {resumo.length === 0 ? (
              <tr><td colSpan="4" className="text-center py-10 text-gray-400 font-medium">Nenhum dado log칤stico dispon칤vel</td></tr>
            ) : (
              resumo.map(([reg, dados]) => (
                <tr key={reg} className="hover:bg-gray-50 transition-colors">
                  <td className="px-6 py-4 font-bold text-blue-900">{reg}</td>
                  <td className="px-6 py-4 text-center font-medium">{dados.totalPedidos}</td>
                  <td className="px-6 py-4 text-center font-medium">{dados.clientes.size}</td>
                  <td className="px-6 py-4 text-right font-black text-blue-600">{dados.totalMetros.toFixed(2)}m</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

/* ===================== COMPONENTE SETTINGS ===================== */
const Settings = ({ metrosPorHora, setMetrosPorHora }) => {
  return (
    <div className="space-y-6 animate-in fade-in duration-500">
      <div className="bg-white p-6 rounded-2xl shadow-sm border border-gray-100">
        <h3 className="text-xl font-black mb-4 text-blue-900 flex items-center gap-2">
          <SettingsIcon size={24} /> Configura칞칫es de Produtividade
        </h3>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {Object.entries(metrosPorHora).map(([tipo, metros]) => (
            <div key={tipo} className="bg-gray-50 p-4 rounded-xl border border-gray-200">
              <label className="block text-xs font-bold uppercase text-gray-400 mb-2 tracking-wider">{tipo}</label>
              <div className="flex items-center gap-3">
                <input 
                  type="number" 
                  value={metros} 
                  onChange={(e) => setMetrosPorHora({...metrosPorHora, [tipo]: parseFloat(e.target.value) || 0})} 
                  className="w-full border-gray-200 border rounded-xl px-4 py-2.5 focus:ring-2 focus:ring-blue-500 outline-none transition-all font-bold text-blue-900" 
                />
                <span className="text-sm font-black text-gray-400">m/h</span>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

/* ===================== APP ===================== */
const App = () => {
  const [activeTab, setActiveTab] = useState('pedidos');
  const [pedidos, setPedidos] = useLocalStorage('pedidos-mestre-aco', []);
  const [metrosPorHora, setMetrosPorHora] = useLocalStorage('config-produtividade', PRODUCAO_MAQUINAS_PADRAO);

  const [editingPedido, setEditingPedido] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [loadingCep, setLoadingCep] = useState(false);

  const [formData, setFormData] = useState({
    dataEntrada: hojeISO(),
    pedido: '',
    cliente: '',
    vendedor: VENDEDORES[0],
    tipoTelha: TIPOS_TELHAS[0],
    totalMetros: '',
    maquina: MAQUINAS[0],
    colagem: 'NENHUMA',
    tipoEps: TIPOS_EPS[0],
    tipoFrete: TIPOS_FRETE[0],
    tipoEntrega: 'ENTREGA', // 'ENTREGA' ou 'RETIRADA'
    cep: '',
    regiao: '',
    dataEntregaCliente: '',
    dataPrevistaProducao: '',
    diasSugeridos: '',
    horaMaquina: '',
    horaMontagem: '',
    tempoTotalProducao: ''
  });

  // Busca CEP via API
  useEffect(() => {
    const buscarCep = async () => {
      const cepLimpo = formData.cep.replace(/\D/g, '');
      if (cepLimpo.length === 8 && formData.tipoEntrega === 'ENTREGA') {
        setLoadingCep(true);
        try {
          const response = await fetch(`https://viacep.com.br/ws/${cepLimpo}/json/`);
          const data = await response.json();
          if (!data.erro) {
            setFormData(prev => ({ ...prev, regiao: `${data.localidade} - ${data.uf}` }));
          } else {
            alert('CEP n칚o encontrado.');
          }
        } catch (error) {
          console.error('Erro ao buscar CEP', error);
        } finally {
          setLoadingCep(false);
        }
      }
    };
    const timer = setTimeout(buscarCep, 800);
    return () => clearTimeout(timer);
  }, [formData.cep, formData.tipoEntrega]);

  const calcularMetricas = (pedido) => {
    const metros = parseFloat(pedido.totalMetros) || 0;

    // Hora M치quina
    const prodHora = metrosPorHora[pedido.maquina] || 0;
    const horaMaquina = prodHora > 0 ? metros / prodHora : 0;

    // Hora Montagem (Colagem)
    let horaMontagem = 0;
    // No exemplo: TP40 30mm -> 1000m / 90 m/h = 11.11h
    // Vamos usar a l칩gica de colagem baseada no tipo de telha e EPS se n칚o estiver expl칤cito
    const chaveColagem = `${pedido.tipoTelha}_${pedido.tipoEps.replace('MM', '')}`;
    const prodColagem = COLAGEM_PRODUCAO[chaveColagem] || COLAGEM_PRODUCAO['TP40_30'];
    horaMontagem = prodColagem > 0 ? metros / prodColagem : 0;

    const tempoTotal = horaMaquina + horaMontagem;

    // C치lculo da Data Sugerida conforme o exemplo
    const { dataSugerida, diasSugeridos } = calcularDataSugerida(pedido.dataEntrada, tempoTotal);

    return {
      horaMaquina: horaMaquina.toFixed(2),
      horaMontagem: horaMontagem.toFixed(2),
      tempoTotal: tempoTotal.toFixed(2),
      dataPrevista: dataSugerida,
      diasSugeridos: diasSugeridos,
    };
  };

  // Atualiza m칠tricas quando campos relevantes mudam
  useEffect(() => {
    if (formData.totalMetros && formData.dataEntrada) {
      const metricas = calcularMetricas(formData);
      setFormData(prev => ({
        ...prev,
        horaMaquina: metricas.horaMaquina,
        horaMontagem: metricas.horaMontagem,
        tempoTotalProducao: metricas.tempoTotal,
        dataPrevistaProducao: metricas.dataPrevista,
        diasSugeridos: metricas.diasSugeridos
      }));
    }
  }, [formData.totalMetros, formData.dataEntrada, formData.maquina, formData.tipoTelha, formData.tipoEps]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.pedido || !formData.cliente || !formData.totalMetros) {
      alert('Preencha os campos obrigat칩rios.');
      return;
    }

    const novoPedido = {
      ...formData,
      id: editingPedido?.id || Date.now(),
    };

    if (editingPedido) {
      setPedidos(pedidos.map(p => p.id === editingPedido.id ? novoPedido : p));
    } else {
      setPedidos([...pedidos, novoPedido]);
    }
    resetForm();
  };

  const resetForm = () => {
    setFormData({
      dataEntrada: hojeISO(),
      pedido: '',
      cliente: '',
      vendedor: VENDEDORES[0],
      tipoTelha: TIPOS_TELHAS[0],
      totalMetros: '',
      maquina: MAQUINAS[0],
      colagem: 'NENHUMA',
      tipoEps: TIPOS_EPS[0],
      tipoFrete: TIPOS_FRETE[0],
      tipoEntrega: 'ENTREGA',
      cep: '',
      regiao: '',
      dataEntregaCliente: '',
      dataPrevistaProducao: '',
      diasSugeridos: '',
      horaMaquina: '',
      horaMontagem: '',
      tempoTotalProducao: ''
    });
    setEditingPedido(null);
    setShowForm(false);
  };

  const handleEdit = (pedido) => {
    setFormData(pedido);
    setEditingPedido(pedido);
    setShowForm(true);
  };

  const handleDelete = (id) => {
    if (window.confirm('Excluir este pedido?')) {
      setPedidos(pedidos.filter(p => p.id !== id));
    }
  };

  const exportarDados = () => {
    const dataToExport = { pedidos, metrosPorHora, exportDate: new Date().toISOString() };
    const blob = new Blob([JSON.stringify(dataToExport, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `mestre-aco-logistica-${new Date().toISOString().split('T')[0]}.json`;
    a.click();
    URL.revokeObjectURL(url);
  };

  const importarDados = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        try {
          const dados = JSON.parse(event.target.result);
          if (Array.isArray(dados.pedidos)) {
            setPedidos(dados.pedidos);
            if (dados.metrosPorHora) setMetrosPorHora(dados.metrosPorHora);
          }
        } catch (error) { alert('Erro ao importar.'); }
      };
      reader.readAsText(file);
    }
  };

  const totalHorasDisponiveisMes = HORAS_DIA * DIAS_UTEIS_MES * MAQUINAS.length;
  const resumoDashboard = useMemo(() => {
    const porMaquina = {};
    MAQUINAS.forEach(m => porMaquina[m] = { horasUsadas: 0, pedidos: [] });
    (Array.isArray(pedidos) ? pedidos : []).forEach(p => {
      if (!porMaquina[p.maquina]) return;
      porMaquina[p.maquina].horasUsadas += Number(p.tempoTotalProducao || 0);
      porMaquina[p.maquina].pedidos.push(p);
    });
    const resultado = {};
    MAQUINAS.forEach(m => {
      const capacidade = HORAS_DIA * DIAS_UTEIS_MES;
      const usadas = porMaquina[m].horasUsadas;
      resultado[m] = { capacidade, usadas, saldo: capacidade - usadas, pedidos: porMaquina[m].pedidos };
    });
    return resultado;
  }, [pedidos]);

  const totalHorasUsadas = (Array.isArray(pedidos) ? pedidos : []).reduce((acc, p) => acc + Number(p.tempoTotalProducao || 0), 0);
  const saldoHoras = totalHorasDisponiveisMes - totalHorasUsadas;

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-blue-900 text-white p-6 shadow-xl">
        <div className="max-w-7xl mx-auto flex flex-col md:flex-row justify-between items-center gap-4">
          <div className="flex items-center gap-4">
            <div className="bg-white p-2 rounded-xl shadow-inner"><Package className="text-blue-900" size={32} /></div>
            <div>
              <h1 className="text-3xl font-black tracking-tight">MESTRE A칂O SP</h1>
              <p className="text-blue-200 text-sm font-medium uppercase tracking-widest">Log칤stica & Produ칞칚o v5.2</p>
            </div>
          </div>
          <nav className="flex bg-blue-800/50 p-1 rounded-xl backdrop-blur-sm">
            <button onClick={() => setActiveTab('pedidos')} className={`flex items-center gap-2 px-6 py-2 rounded-lg transition-all ${activeTab === 'pedidos' ? 'bg-white text-blue-900 shadow-lg' : 'text-blue-100 hover:bg-blue-700/50'}`}><Package size={18} /> <span className="font-bold">Pedidos</span></button>
            <button onClick={() => setActiveTab('dashboard')} className={`flex items-center gap-2 px-6 py-2 rounded-lg transition-all ${activeTab === 'dashboard' ? 'bg-white text-blue-900 shadow-lg' : 'text-blue-100 hover:bg-blue-700/50'}`}><BarChart3 size={18} /> <span className="font-bold">Dashboard</span></button>
            <button onClick={() => setActiveTab('regioes')} className={`flex items-center gap-2 px-6 py-2 rounded-lg transition-all ${activeTab === 'regioes' ? 'bg-white text-blue-900 shadow-lg' : 'text-blue-100 hover:bg-blue-700/50'}`}><MapPin size={18} /> <span className="font-bold">Regi칫es</span></button>
            <button onClick={() => setActiveTab('config')} className={`flex items-center gap-2 px-6 py-2 rounded-lg transition-all ${activeTab === 'config' ? 'bg-white text-blue-900 shadow-lg' : 'text-blue-100 hover:bg-blue-700/50'}`}><SettingsIcon size={18} /> <span className="font-bold">Config</span></button>
          </nav>
        </div>
      </header>

      <main className="max-w-7xl mx-auto p-4 md:p-6">
        {activeTab === 'pedidos' && (
          <div className="space-y-6 animate-in fade-in duration-500">
            <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4">
              <h2 className="text-2xl font-bold text-gray-800">Carteira de Pedidos</h2>
              <div className="flex flex-wrap gap-2">
                <button onClick={() => setShowForm(true)} className="flex items-center gap-2 bg-blue-600 text-white px-5 py-2.5 rounded-xl hover:bg-blue-700 transition-colors shadow-md font-bold"><Plus size={20} /> Novo Pedido</button>
                <button onClick={exportarDados} className="flex items-center gap-2 bg-emerald-600 text-white px-5 py-2.5 rounded-xl hover:bg-emerald-700 transition-colors shadow-md font-bold"><Download size={20} /> Exportar</button>
                <label className="flex items-center gap-2 bg-amber-600 text-white px-5 py-2.5 rounded-xl hover:bg-amber-700 transition-colors shadow-md font-bold cursor-pointer"><Upload size={20} /> Importar<input type="file" accept=".json" onChange={importarDados} className="hidden" /></label>
              </div>
            </div>

            {showForm && (
              <div className="bg-white p-6 rounded-2xl shadow-2xl border-2 border-blue-100 animate-in zoom-in duration-300">
                <div className="flex justify-between items-center mb-6">
                  <h3 className="text-xl font-black text-blue-900">{editingPedido ? '游닇 Editar Pedido' : '游 Novo Pedido'}</h3>
                  <button onClick={resetForm} className="p-2 hover:bg-gray-100 rounded-full transition-colors text-gray-400"><X size={24} /></button>
                </div>
                <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div className="space-y-1"><label className="text-xs font-bold text-gray-500 uppercase ml-1">Data Entrada</label><input type="date" value={formData.dataEntrada} onChange={(e) => setFormData({ ...formData, dataEntrada: e.target.value })} className="w-full border-gray-200 border rounded-xl px-4 py-2.5 focus:ring-2 focus:ring-blue-500 outline-none transition-all" /></div>
                  <div className="space-y-1"><label className="text-xs font-bold text-gray-500 uppercase ml-1">N췈 Pedido *</label><input type="text" required value={formData.pedido} onChange={(e) => setFormData({ ...formData, pedido: e.target.value })} className="w-full border-gray-200 border rounded-xl px-4 py-2.5 focus:ring-2 focus:ring-blue-500 outline-none transition-all" placeholder="Ex: 12345" /></div>
                  <div className="space-y-1"><label className="text-xs font-bold text-gray-500 uppercase ml-1">Cliente *</label><input type="text" required value={formData.cliente} onChange={(e) => setFormData({ ...formData, cliente: e.target.value })} className="w-full border-gray-200 border rounded-xl px-4 py-2.5 focus:ring-2 focus:ring-blue-500 outline-none transition-all" placeholder="Nome do cliente" /></div>

                  <div className="space-y-1">
                    <label className="text-xs font-bold text-gray-500 uppercase ml-1">Vendedor</label>
                    <div className="relative">
                      <select value={formData.vendedor} onChange={(e) => setFormData({ ...formData, vendedor: e.target.value })} className="w-full border-gray-200 border rounded-xl px-4 py-2.5 focus:ring-2 focus:ring-blue-500 outline-none transition-all bg-white appearance-none font-bold text-blue-900">
                        {VENDEDORES.map(v => <option key={v} value={v}>{v}</option>)}
                      </select>
                      <div className="absolute right-4 top-3 text-gray-400 pointer-events-none"><User size={18}/></div>
                    </div>
                  </div>

                  <div className="space-y-1">
                    <label className="text-xs font-bold text-gray-500 uppercase ml-1">Tipo de Entrega</label>
                    <div className="flex gap-2">
                      <button type="button" onClick={() => setFormData({...formData, tipoEntrega: 'ENTREGA'})} className={`flex-1 flex items-center justify-center gap-2 py-2.5 rounded-xl border-2 transition-all font-bold ${formData.tipoEntrega === 'ENTREGA' ? 'border-blue-600 bg-blue-50 text-blue-600' : 'border-gray-100 text-gray-400'}`}><Truck size={18}/> Entrega</button>
                      <button type="button" onClick={() => setFormData({...formData, tipoEntrega: 'RETIRADA', cep: '', regiao: 'RETIRADA NA LOJA'})} className={`flex-1 flex items-center justify-center gap-2 py-2.5 rounded-xl border-2 transition-all font-bold ${formData.tipoEntrega === 'RETIRADA' ? 'border-amber-600 bg-amber-50 text-amber-600' : 'border-gray-100 text-gray-400'}`}><Store size={18}/> Retirada</button>
                    </div>
                  </div>

                  {formData.tipoEntrega === 'ENTREGA' && (
                    <>
                      <div className="space-y-1">
                        <label className="text-xs font-bold text-gray-500 uppercase ml-1">CEP de Entrega</label>
                        <div className="relative">
                          <input type="text" value={formData.cep} onChange={(e) => setFormData({ ...formData, cep: e.target.value })} className="w-full border-gray-200 border rounded-xl px-4 py-2.5 focus:ring-2 focus:ring-blue-500 outline-none transition-all" placeholder="00000-000" />
                          {loadingCep && <div className="absolute right-3 top-3 animate-spin rounded-full h-4 w-4 border-b-2 border-blue-600"></div>}
                        </div>
                      </div>
                      <div className="space-y-1">
                        <label className="text-xs font-bold text-gray-500 uppercase ml-1">Regi칚o / Cidade</label>
                        <input type="text" value={formData.regiao} readOnly className="w-full bg-gray-50 border-gray-200 border rounded-xl px-4 py-2.5 text-gray-500 font-bold" placeholder="Auto-preenchido pelo CEP" />
                      </div>
                    </>
                  )}

                  <div className="space-y-1"><label className="text-xs font-bold text-gray-500 uppercase ml-1">M치quina</label><select value={formData.maquina} onChange={(e) => setFormData({ ...formData, maquina: e.target.value })} className="w-full border-gray-200 border rounded-xl px-4 py-2.5 focus:ring-2 focus:ring-blue-500 outline-none transition-all bg-white">{MAQUINAS.map(m => <option key={m} value={m}>{m}</option>)}</select></div>
                  <div className="space-y-1"><label className="text-xs font-bold text-gray-500 uppercase ml-1">Total Metros *</label><input type="number" step="0.01" required value={formData.totalMetros} onChange={(e) => setFormData({ ...formData, totalMetros: e.target.value })} className="w-full border-gray-200 border rounded-xl px-4 py-2.5 focus:ring-2 focus:ring-blue-500 outline-none transition-all" placeholder="0.00" /></div>
                  <div className="space-y-1"><label className="text-xs font-bold text-gray-500 uppercase ml-1">Data Sugerida</label><input type="date" disabled value={formData.dataPrevistaProducao || ''} className="w-full bg-blue-50 border-blue-200 border rounded-xl px-4 py-2.5 font-bold text-blue-700" /></div>

                  <div className="md:col-span-3 grid grid-cols-2 md:grid-cols-4 gap-4 p-4 bg-gray-50 rounded-xl border border-gray-100">
                    <div className="text-center"><p className="text-[10px] font-bold text-gray-400 uppercase">Hora M치quina</p><p className="text-lg font-black text-blue-900">{formData.horaMaquina || '0.00'}</p></div>
                    <div className="text-center"><p className="text-[10px] font-bold text-gray-400 uppercase">Hora Montagem</p><p className="text-lg font-black text-blue-900">{formData.horaMontagem || '0.00'}</p></div>
                    <div className="text-center"><p className="text-[10px] font-bold text-gray-400 uppercase">Tempo Total</p><p className="text-lg font-black text-blue-900">{formData.tempoTotalProducao || '0.00'}</p></div>
                    <div className="text-center"><p className="text-[10px] font-bold text-gray-400 uppercase">Sugest칚o Prazo</p><p className="text-lg font-black text-green-600">{formData.diasSugeridos || '0.00'} dias</p></div>
                  </div>

                  <div className="md:col-span-3 flex justify-end gap-3 mt-4 pt-4 border-t">
                    <button type="button" onClick={resetForm} className="px-6 py-2.5 border border-gray-300 rounded-xl hover:bg-gray-50 transition-colors font-bold text-gray-600">Cancelar</button>
                    <button type="submit" className="flex items-center gap-2 bg-blue-900 text-white px-8 py-2.5 rounded-xl hover:bg-blue-800 transition-colors shadow-lg font-bold"><Save size={20} /> Salvar Pedido</button>
                  </div>
                </form>
              </div>
            )}

            <div className="bg-white rounded-2xl shadow-sm border border-gray-100 overflow-hidden">
              <div className="overflow-x-auto">
                <table className="w-full text-sm text-left">
                  <thead>
                    <tr className="bg-gray-50 border-b border-gray-100">
                      <th className="px-4 py-4 font-bold text-gray-600">Pedido</th>
                      <th className="px-4 py-4 font-bold text-gray-600">Cliente</th>
                      <th className="px-4 py-4 font-bold text-gray-600">Vendedor</th>
                      <th className="px-4 py-4 font-bold text-gray-600">Entrega / Regi칚o</th>
                      <th className="px-4 py-4 font-bold text-gray-600 text-right">Metros</th>
                      <th className="px-4 py-4 font-bold text-gray-600 text-right">Data Sugerida</th>
                      <th className="px-4 py-4 font-bold text-gray-600 text-center">A칞칫es</th>
                    </tr>
                  </thead>
                  <tbody className="divide-y divide-gray-50">
                    {(Array.isArray(pedidos) ? pedidos : []).map((p) => (
                      <tr key={p.id} className="hover:bg-blue-50/30 transition-colors group">
                        <td className="px-4 py-4 font-black text-blue-900">{p.pedido}</td>
                        <td className="px-4 py-4 font-medium text-gray-700">{p.cliente}</td>
                        <td className="px-4 py-4 font-bold text-gray-500 text-xs">{p.vendedor}</td>
                        <td className="px-4 py-4">
                          <div className="flex items-center gap-2">
                            {p.tipoEntrega === 'RETIRADA' ? <Store size={14} className="text-amber-600"/> : <Truck size={14} className="text-blue-600"/>}
                            <span className={`text-xs font-bold ${p.tipoEntrega === 'RETIRADA' ? 'text-amber-700' : 'text-blue-700'}`}>
                              {p.tipoEntrega === 'RETIRADA' ? 'RETIRADA' : p.regiao}
                            </span>
                          </div>
                        </td>
                        <td className="px-4 py-4 text-right font-bold">{parseFloat(p.totalMetros).toFixed(2)}m</td>
                        <td className="px-4 py-4 text-right font-bold text-green-700">{formatarDataBR(p.dataPrevistaProducao)}</td>
                        <td className="px-4 py-4">
                          <div className="flex justify-center gap-1 opacity-0 group-hover:opacity-100 transition-opacity">
                            <button onClick={() => handleEdit(p)} className="p-2 text-blue-600 hover:bg-blue-100 rounded-lg transition-colors"><Edit2 size={18} /></button>
                            <button onClick={() => handleDelete(p.id)} className="p-2 text-red-600 hover:bg-red-100 rounded-lg transition-colors"><Trash2 size={18} /></button>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        )}

        {activeTab === 'dashboard' && (
          <Dashboard resumoPorMaquina={resumoDashboard} totalHorasDisponiveis={totalHorasDisponiveisMes} totalHorasUsadas={totalHorasUsadas} saldoHoras={saldoHoras} />
        )}

        {activeTab === 'regioes' && <RelatorioRegioes pedidos={pedidos} />}

        {activeTab === 'config' && (
          <Settings metrosPorHora={metrosPorHora} setMetrosPorHora={setMetrosPorHora} />
        )}
      </main>

      <footer className="max-w-7xl mx-auto p-6 text-center text-gray-400 text-xs font-medium uppercase tracking-widest">
        &copy; {new Date().getFullYear()} Mestre A칞o SP - Sistema de Log칤stica Integrada
      </footer>
    </div>
  );
};

export default App;
